import { NextRequest, NextResponse } from 'next/server'
import { requireApiAuth } from '@/lib/api-auth'
import { prisma } from '@/lib/prisma'

export const dynamic = 'force-dynamic'

// GET /api/reports/generated - Get list of previously generated reports
export async function GET(request: NextRequest) {
  try {
    const authResult = await requireApiAuth(request)
    if (authResult instanceof NextResponse) return authResult

    // Get reports generated by the current user, ordered by most recent first
    const reports = await prisma.generatedReport.findMany({
      where: {
        generatedById: authResult.user.id
      },
      orderBy: {
        createdAt: 'desc'
      },
      take: 50 // Limit to last 50 reports
    })

    // Transform to Android-compatible format
    const response = reports.map(report => ({
      id: report.id,
      name: report.name,
      type: report.type,
      status: report.status,
      format: report.format,
      description: report.description,
      projectId: report.projectId,
      projectName: report.projectName,
      period: report.period,
      startDate: report.startDate?.toISOString() || null,
      endDate: report.endDate?.toISOString() || null,
      fileUrl: report.fileUrl,
      chartType: report.chartType,
      stats: report.stats,
      data: report.data,
      createdAt: report.createdAt.toISOString()
    }))

    return NextResponse.json(response)
  } catch (error) {
    console.error('Error fetching generated reports:', error)
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 })
  }
}
